@AccessControl.authorizationCheck: #NOT_REQUIRED
@EndUserText.label: 'Costing Total'
@Metadata.ignorePropagatedAnnotations: true
define root view entity ZETR_DDL_I_COST_TOTAL
  as select from I_JournalEntryItem     as _JournalItem
    inner join   zetr_t_exp130          as CostingReport    on  CostingReport.company_code  = _JournalItem.CompanyCode
                                                            and CostingReport.journal_entry = _JournalItem.AccountingDocument
                                                            and CostingReport.fiscal_year   = _JournalItem.FiscalYear

    inner join   ZETR_DDL_I_EXP_ACCOUNT as ExportAccounting on ExportAccounting.Saknr = _JournalItem.GLAccount

{
  key CostingReport.company_code                     as CompanyCode,
  key CostingReport.journal_entry                    as JournalEntry,
  key CostingReport.fiscal_year                      as FiscalYear,
      _JournalItem.TransactionCurrency               as TransactionCurrency,
      @Aggregation.default: #SUM
      @Semantics: { amount : {currencyCode: 'TransactionCurrency'} }
      sum( _JournalItem.AmountInTransactionCurrency) as CostTotal


}
where
  _JournalItem.Ledger = '0L'

group by
  CostingReport.company_code,
  CostingReport.journal_entry,
  CostingReport.fiscal_year,
  _JournalItem.TransactionCurrency

