@AccessControl.authorizationCheck: #NOT_REQUIRED
@EndUserText.label: 'Export texts'
@Metadata.allowExtensions: true
define view entity ZETR_DDL_I_EXP_TXT
  as select from    zetr_t_exp126        as TextTable
    left outer join zetr_t_exp123        as Main           on Main.objecttype = TextTable.text_key
    left outer join I_TextObjectTypeText as TextType       on  TextType.TextObjectType     = Main.objecttype
                                                           and TextType.TextObjectCategory = TextTable.object_category
                                                           and TextType.Language           = $session.system_language
    inner join      zetr_t_r101          as DocumentHeader on DocumentHeader.filen = Main.filen
  association to parent ZETR_DDL_I_EXP_HEAD as _ExportFile on $projection.filen = _ExportFile.Filen
{

      //@OData.property.name: 'filen'
  key Main.filen                  as filen,
      @ObjectModel.text.element:['TextName']
      @UI.textArrangement:#TEXT_FIRST
  key TextTable.text_key          as Objecttype,
      Main.text                   as Text,
      TextType.TextObjectTypeName as TextName,
      TextType.Language           as Lang,
      _ExportFile

}
where
  DocumentHeader.isdeleted is initial
